import React, {useRef, useState, useEffect, forwardRef} from "react";


import '../Css/ListaDeGatos.css';
import GatoFormulario from "./GatoFormulario";

function ListaDeGatos(props, ref){

  const [textoG, setTextoG] = useState([]);
  const referencia = useRef(null);
  const [mountedT, setMountedT] = useState(false);

  useEffect(() => {
    if(referencia.current && mountedT === true){
      referencia.current.scrollTop = referencia.current.scrollHeight;
    };
  }, [textoG, mountedT]);


  const [claseGato, setClaseGato] = useState("");
  const [mountedClase, setMountedClase] = useState(false);
  const claseAsesino = () => {
    setClaseGato("Asesino");
  };
  const claseGuerrero = () => {
    setClaseGato("Guerrero");
  };
  const claseHealer = () => {
    setClaseGato("Healer");
  };
  const claseMago = () => {
    setClaseGato("Mago");
  };
  const claseTanque = () => {
    setClaseGato("Tanque");
  };

  const [armaGato, setArmaGato] = useState("");
  const [mountedArma, setMountedArma] = useState(false);
  const armaDaga = () => {
    setArmaGato("Daga");
  };
  const armaEspada = () => {
    setArmaGato("Espada");
  };
  const armaArco = () => {
    setArmaGato("Arco");
  };
  const armaBaculo = () => {
    setArmaGato("Baculo");
  };
  const armaMandoble = () => {
    setArmaGato("Mandoble");
  };
  useEffect(() => {
    if(mountedClase === true && mountedArma === true){
      console.log("Se ha creado un Gato " + claseGato + " con " + armaGato);
      setMountedClase(false);
      setMountedArma(false);
    }
  }, [claseGato, armaGato, mountedClase, mountedArma])


  const crearGato = () => {
    if(claseGato !== "" && armaGato !== ""){
      setTextoG("NOMBRE-RANDOM");
      setMountedT(true);
      setMountedArma(true);
      setMountedClase(true);
    };
  };

  return(
    <div className="gato-rpg">
      <GatoFormulario/>
      <div
      className="contenedor-lista"
      ref={ref}>
        <div
        className="contenedor-gatos">
          {props.textoG}
        </div>
        <div
        className="contenedor-gatos">
          {props.textoG}
        </div>
        <div
        className="contenedor-gatos">
          {props.textoG}
        </div>
        <div
        className="contenedor-gatos">
          {props.textoG}
        </div>
        
      </div>
  </div>
  );
};

export default forwardRef(ListaDeGatos);
