import React, {useState, useEffect} from "react";

import '../Css/PiedraPapelTijeraConCSS.css';

function PiedraPapelTijeraConCSS(){

  const [ piPaTiRND, setPiPaTiRND ] = useState("");
  const [ permitirUseEffect, setPermitirUseEffect ] = useState(false);

  const divP1 = document.querySelector(".contenedor21");
  const divP2 = document.querySelector(".contenedor31");

  function BotonClick(){
    const calculo = Math.floor(Math.random() * 3) + 1;
    console.log(calculo);
    if ( calculo === 1 ){
      setPiPaTiRND("Piedra");
      setPermitirUseEffect(true);
    }else if ( calculo === 2 ){
      setPiPaTiRND("Papel");
      setPermitirUseEffect(true);
    }else if ( calculo === 3 ){
      setPiPaTiRND("Tijera");
      setPermitirUseEffect(true);
    };
  };


  useEffect(() =>{
    if( permitirUseEffect === true && piPaTiRND !== "" ){
      console.log(piPaTiRND);
      console.log(divP1.textContent);
      console.log(divP2.textContent);
    };
  },[permitirUseEffect, piPaTiRND, divP1, divP2]);


  return(
    <div className="cuerpo">
      <div className="contenedorContenedores">
        <div className="contenedor1">
        1
        </div>
        <div>
          <div className="contenedor20">
            2.0
          </div>
          <div className="contenedor21">
            2.1
          </div>
        </div>
        <div>
          <div className="contenedor30">
            3.0
          </div>
          <div className="contenedor31">
            3.1
          </div>
        </div>
        <div className="contenedor4">
        4
        </div>
      </div>
      <div>
        <button
          className="tamaÃ±oBoton"
          onClick={BotonClick}>
          Iniciar
        </button>
      </div>
    </div>
  )
};

export default PiedraPapelTijeraConCSS;
