import React, {useEffect, useRef, useState} from "react";

import TextoColRND from '../Componentes/TextoColRND.jsx';
import CaraOSello from "../Componentes/CaraOSello.jsx";

import '../Css/ProyectosRND.css';
import ClaseGatos from "../Componentes/ClaseGatos.jsx";

function ProyectosRND(){

  const [colorRND, setColorRND] = useState({});
  const aplicarEstilo = () => {
    const rojo = Math.floor(Math.random() * 256);
    const verde = Math.floor(Math.random() * 256);
    const azul = Math.floor(Math.random() * 256);
  
    const color = `rgb(${rojo}, ${verde}, ${azul})`;

    const nuevoEstilo = {
      color: color
    };

    setColorRND(nuevoEstilo);
  };

  
  const [historial, setHistorial] = useState([]);

  const [calculoResultado, setcalculoResultado] = useState('');
  const caraOSelloRandom = () => {
    const calculo = Math.floor(Math.random() * 2);
    console.log(calculo);

    if(calculo === 0){
      setcalculoResultado("Cara");
    }else if(calculo === 1){
      setcalculoResultado("Sello");
    };
    setMountedH(true);
  };
  const [mountedH, setMountedH] = useState(false);
  useEffect(() => {
    if (mountedH === true){
      setHistorial([...historial, calculoResultado]);
      setMountedH(false);
    };
  }, [historial, calculoResultado, mountedH]); // Establece las dependencias

  const [resultado, setresultado] = useState('');

  const [respuestaCaraOSello, setrespuestaCaraOSello] = useState('');
  const [nombre, setnombre] = useState('');

  const enviar = () => {
    const nombreTxt = document.querySelector("#nombre");
    setnombre(nombreTxt.value);

    const respuestaTxt = document.querySelector("#respuesta");
    setrespuestaCaraOSello(respuestaTxt.value);
    console.log(respuestaCaraOSello);

    caraOSelloRandom();
    console.log(calculoResultado);
    console.log();
    setMounted1(true);
  };
  const [mounted1, setMounted1] = useState(false);
  useEffect(() => {
    // Esta funci贸n se ejecuta cuando caraOSelloTxt cambia
    if (mounted1 === true) {
      if((respuestaCaraOSello === calculoResultado)){
        console.log("acertado");
        setresultado(`La respuesta de ${nombre} ha sido ${respuestaCaraOSello} y ha acertado`);
      }else if (respuestaCaraOSello !== calculoResultado){
        console.log("fallado");
        setresultado(`La respuesta de ${nombre} ha sido ${respuestaCaraOSello} y ha fallado`);
      };
    };
  }, [respuestaCaraOSello, nombre, calculoResultado, mounted1]); // Establece las dependencias

  const [caraOSelloTxt, setcaraOSello] = useState('');
  const cara = () => {
    console.log("cara");

    setcaraOSello("Cara");

    caraOSelloRandom();
    setMounted(true);
  };

  const sello = () => {
    console.log("sello");
    setcaraOSello("Sello");

    caraOSelloRandom();
    setMounted(true);
  };
  const [mounted, setMounted] = useState(false);
  useEffect(() => {
    // Esta funci贸n se ejecuta cuando caraOSelloTxt cambia
    if (mounted === true) {
      if((caraOSelloTxt === calculoResultado)){
        console.log("acertado");
        setresultado(`Tu respuesta ha sido ${caraOSelloTxt} y has acertado`);
      }else{
        console.log("fallado");
        setresultado(`Tu respuesta ha sido ${caraOSelloTxt} y has fallado`);
      };
    };
  }, [caraOSelloTxt, calculoResultado, mounted]); // Establece las dependencias


  const [textoG, setTextoG] = useState([]);
  const referencia = useRef(null);


  const [mountedT, setMountedT] = useState(false);
  const agregarGato = () => {
    setTextoG([...textoG, "Paaaaaaaaa"]);
    setMountedT(true);
    console.log("mutedT");
  };

  useEffect(() => {
    if(referencia.current && mountedT === true){
      referencia.current.scrollTop = referencia.current.scrollHeight;
    };
  }, [textoG, mountedT]);


  const [claseGato, setClaseGato] = useState("");
  const [mountedClase, setMountedClase] = useState(false);
  const claseAsesino = () => {
    setClaseGato("Asesino");
  };
  const claseGuerrero = () => {
    setClaseGato("Guerrero");
  };
  const claseHealer = () => {
    setClaseGato("Healer");
  };
  const claseMago = () => {
    setClaseGato("Mago");
  };
  const claseTanque = () => {
    setClaseGato("Tanque");
  };
  useEffect(() => {
    if(mountedClase === true){
      console.log(claseGato);
      setMountedClase(false);
    };
  }, [claseGato, mountedClase]);

  const [armaGato, setArmaGato] = useState("");
  const [mountedArma, setMountedArma] = useState(false);
  const armaDaga = () => {
    setArmaGato("Daga");
  };
  const armaEspada = () => {
    setArmaGato("Espada");
  };
  const armaArco = () => {
    setArmaGato("Arco");
  };
  const armaBaculo = () => {
    setArmaGato("Baculo");
  };
  const armaMandoble = () => {
    setArmaGato("Mandoble");
  };
  useEffect(() => {
    if(mountedArma === true){
      console.log(armaGato);
      setMountedArma(false);
    }
  }, [])

  return(
    <div>
      <div className='contenedor-experimentos'>
        <TextoColRND
          texto = "lorem lorem lorem lorem lorem lorem lorem lorem lorem lorem lorem lorem lorem lorem lorem lorem lorem lorem lorem lorem"
          color = {colorRND}
          colorRND={aplicarEstilo} />
        <CaraOSello
          enviar={enviar}
          cara={cara}
          sello={sello}
          respuesta={`${resultado}`}
          resultadoCalculo={`${calculoResultado}`}
          historial={`${historial}`} />
        <ClaseGatos
          ref={referencia}
          asesino={claseAsesino}
          guerrero={claseGuerrero}
          healer={claseHealer}
          mago={claseMago}
          tanque={claseTanque}
          daga={armaDaga}
          espada={armaEspada}
          arco={armaArco}
          baculo={armaBaculo}
          mandoble={armaMandoble} />
        <div className='imagen-random-gatoAPI'>
          <label>3.0</label>
        </div>
        
        <div
          className='chat'>
          <label>4.0</label>
        </div>
      </div>
    </div>
  );
};

export default ProyectosRND;
