import React, { useEffect,useState } from "react";

import BtnPiePapTijRnd from "../Componentes/PiePapTijRnd.jsx";

import '../Css/PiedraPapelTijera.css';

function PiedraPapelTijera() {


  const [ piePapTijRND, setpiePapTijRND ] = useState("");
  const [mountedH, setMountedH] = useState(false);
  
  const saberNombre = document.querySelector("#testeo");

  const random = () => {
    const calculo = Math.floor(Math.random() * 3) + 1;
    if (calculo === 1){
      setpiePapTijRND("piedra");
    } else if (calculo === 2) {
      setpiePapTijRND("papel");
    }else if (calculo === 3){
      setpiePapTijRND("tijera");
    };
    setMountedH(true);

    if (saberNombre.textContent != null){
      console.log(saberNombre.textContent);
    }
  }


  useEffect(() => {
    if (mountedH === true && piePapTijRND !== "" ){
      setMountedH(false);
      console.log(piePapTijRND)
    };
  }, [piePapTijRND, mountedH]); // Establece las dependencias





  return(
    <div className="cuerpo">
      <div className="cont1">
        <div className="piedra1">
          piedra
        </div>
        <div className="papel1">
          papel
        </div>
        <div className="tijera1">
          tijera
        </div>
      </div>
      <div className="cont2">
        <BtnPiePapTijRnd
          id="testeo"
          className="piedra2"
          onClick={random}
          nombre={"Piedra"}/>
        <BtnPiePapTijRnd
          className="papel2"
          onClick={random}
          nombre={"Papel"}/>
        <BtnPiePapTijRnd
          className="tijera2"
          onClick={random}
          nombre={"Tijera"}/>
      </div>
    </div>
  )
}

export default PiedraPapelTijera;
