import React, { useEffect, useState, useRef } from "react";

import MensajePrimChat from "./MensajePrimChat";
import MensajeSeguChat from "./MensajeSeguChat";
import FormularioPrimerChat from "./FormularioPrimerChat";
import FormularioSegundoChat from "./FormularioSegundoChat";

import '../Css/ListaChat.css';

function ListaChat() {

  const referencia = useRef(null);
  const [ mountedMSG1, setMountedMSG1 ] = useState([]);

  useEffect = (() => {
    if( referencia.current && mountedMSG1 === true){
      setMountedMSG1(false);
      referencia.current.scrollTop = referencia.current.scrollHeight;
    };
  }, [mountedMSG1]);

  const [ PrimerosMSGs, setPrimerosMSGs ] = useState([]);

  const agregarPrimerMSG = mensajePrimChat => {
    if(mensajePrimChat.mensaje.trim()){
      mensajePrimChat.mensaje = mensajePrimChat.mensaje.trim()
      setMountedMSG1(true);
      const primerosMSGsActualizados = [ mensajePrimChat, ...PrimerosMSGs];
    };
  }; 

  return(
    <div
    className='Chat'>
      <div className="ContenedorChats">
        <FormularioPrimerChat
          onSubmit = {agregarPrimerMSG}/>
        <FormularioSegundoChat/>
      </div>
      <div className="RegistroChat">
        <div
          className="contenedorHistorial"
          ref={referencia}>
          <MensajePrimChat/>
          <MensajeSeguChat/>
        </div>
      </div>
    </div>
  )
};

export default ListaChat;
