    } else if (calculo === 2) {
      setpiePapTijRND("Papel");
    }else if (calculo === 3){
      setpiePapTijRND("Tijera");
    };
    setMountedH(true);

    setIdDelBoton(`${id}`);
  }

  const [ VidasEnemigo, setVidasEnemigos] = useState(3);
  const [ ImgVidasEnemigo, setImgVidasEnemigos] = useState(Vidas3);

