    //preVidas OBTIENE EL VALOR DE VidasEnemigo PORQUE SE EST츼 USANDO setVidasEnemigos
    setVidasPlayer((preVidasPlayer) => {
      const nuevaVidaPlayer = preVidasPlayer - 1;

