import React, {useState} from "react";

import BotonColRND from '../Componentes/BotonColRND.jsx';

import '../Css/ProyectosRND.css';

function obtenerColorRandom(){
  const rojo = Math.floor(Math.random() * 256);
  const verde = Math.floor(Math.random() * 256);
  const azul = Math.floor(Math.random() * 256);

  const color = `rgb(${rojo}, ${verde}, ${azul})`;

  console.log(color)

  return color;
};

function ProyectosRND(){

  const [colorRND, setColorRND] = useState({});

  // Funci칩n para actualizar el estilo del label
  const aplicarEstilo = () => {
    const nuevoEstilo = {
      color: 'red', // Cambia el color del texto a rojo
      fontSize: '20px', // Cambia el tama침o de fuente a 20px
      fontWeight: 'bold' // Hace que el texto sea negrita
    };

    // Actualiza el estado del estilo
    setLabelStyle(nuevoEstilo);
  };

  return(
    <div>
      <div className='contenedor-experimentos'>
        <BotonColRND
          texto = "lorem lorem lorem lorem lorem lorem lorem lorem lorem lorem lorem lorem lorem lorem lorem lorem lorem lorem lorem lorem"
          colorRND={obtenerColorRandom}
          color = {{color : obtenerColorRandom}}/>

        
        <div className='cara-o-sello'>
          <label>1.2</label>
        </div>

        <div className='gato-rpg'>
          <label>2.0</label>
        </div>

        <div className='imagen-random-gatoAPI'>
          <label>3.0</label>
        </div>

        <div className='chat'>
          <label>4.0</label>
        </div>
      </div>
    </div>
  );
};

export default ProyectosRND;
