import React from "react";
import { v4 as uuidv4 } from 'uuid';

import '../Css/FormulariosChats.css';

function FormularioSegundoChat() {

  const ManejarEnvioPrimForm = e => {
    e.preventDefault();
    const nuevoMensaje = {
      id: uuidv4(),
      fecha: 1000,
      mensaje: "aaa"
    }
    props.onSubmit(nuevoMensaje);
  }

  return(
    <form
      onSubmit={ManejarEnvioPrimForm}>
      <div className="ContenedorTexto">
        <textarea
          className="inputAncho"
          placeholder="ESCRIBE LO QUE QUIERAS"  />
        <button>Enviar</button>
      </div>
    </form>
  )
}

export default FormularioSegundoChat;
