import React, { useEffect,useState } from "react";

import BtnPiePapTijRnd from '../Componentes/PiePapTijRnd.jsx';

import '../Css/PiedraPapelTijera.css';

import GatoTriste from '../Img/GatoTriste.jpg';
import NoHaceNadaHD from '../Img/NoHaceNadaHD.png';

function PiedraPapelTijera() {

  const divImagenIzq = document.querySelector(".divImagenIzq");
  


  const [ piePapTijRND, setpiePapTijRND ] = useState("");
  const [ mountedH, setMountedH ] = useState(false);

  const [ idDelBoton, setIdDelBoton ] = useState("");

  const random = (id) => {
    const calculo = Math.floor(Math.random() * 3) + 1;
    if (calculo === 1){
      setpiePapTijRND("Piedra");
    } else if (calculo === 2) {
      setpiePapTijRND("Papel");
    }else if (calculo === 3){
      setpiePapTijRND("Tijera");
    };
    setMountedH(true);

    setIdDelBoton(`${id}`)
  }




  useEffect(() => {
    if (mountedH === true && piePapTijRND !== "" && idDelBoton !== ""){
      setMountedH(false);
      console.log("BOT: " + piePapTijRND);
      const testeo = document.querySelector("#"+idDelBoton);
      console.log("Escogiste: "+testeo.textContent);

      if (testeo.textContent === piePapTijRND) {
        console.log("EMPATE 🐈");
      } else if (testeo.textContent === "Piedra" && piePapTijRND === "Papel"){
        console.log("Perdiste 😾");
      } else if (testeo.textContent === "Piedra" && piePapTijRND === "Tijera"){
        console.log("Ganaste 😼");
      } else if (testeo.textContent === "Papel" && piePapTijRND === "Tijera"){
        console.log("Perdiste 😾");
      } else if (testeo.textContent === "Papel" && piePapTijRND === "Piedra"){
        console.log("Ganaste 😼");
      } else if (testeo.textContent === "Tijera" && piePapTijRND === "Piedra"){
        console.log("Perdiste 😾");
      } else if (testeo.textContent === "Tijera" && piePapTijRND === "Papel"){
        console.log("Ganaste 😼");
      }
    };
  }, [piePapTijRND, mountedH, idDelBoton]);





  return(
    <div className="cuerpo">
      <div className="contenedorIzquierda">
        <div className="divImagenIzq">
          <label>PERDISTE</label>
          <img
            className="imgIzq"
            src={GatoTriste}
            alt="GatoTriste"/>
        </div>
      </div>
      <div className="contenedorCentro">
        <div className="cont1">
          <div className="piedra1">
            piedra
          </div>
          <div className="papel1">
            papel
          </div>
          <div className="tijera1">
            tijera
          </div>
        </div>
        <div className="cont2">
          <BtnPiePapTijRnd
            id="idPiedra1"
            className="piedra2"
            onClick={(id) => random(id)}
            nombre={"Piedra"}/>
          <BtnPiePapTijRnd
            id="idPapel2"
            className="papel2"
            onClick={random}
            nombre={"Papel"}/>
          <BtnPiePapTijRnd
            id="idTijera2"
            className="tijera2"
            onClick={random}
            nombre={"Tijera"}/>
        </div>
      </div>
      <div className="contenedorDerecha">
        <div className="divImagenDer">
            <label>GANASTE</label>
            <img
              className="imgDer"
              src={NoHaceNadaHD}
              alt="NoHaceNadaHD"/>
          </div>
      </div>
    </div>
  )
}

export default PiedraPapelTijera;
