import React, {useRef, useState, useEffect} from "react";

import MensajePrimChat from "./MensajePrimChat";
import MensajeSeguChat from "./MensajeSeguChat";
import FormularioPrimerChat from "./FormularioPrimerChat";
import FormularioSegundoChat from "./FormularioSegundoChat";

import '../Css/ListaChat.css';

function ListaChat() {

  const [ a, seta ] = useState(0);

  const referencia = useRef(null);

  /* PRIMER CHAT PRIMER CHAT PRIMER CHAT PRIMER CHAT PRIMER CHAT PRIMER CHAT PRIMER CHAT PRIMER CHAT PRIMER CHAT PRIMER CHATPRIMER CHAT  */

  const [ mountedMSG, setMountedMSG ] = useState([]);

  useEffect(() => {
    if( referencia.current && mountedMSG === true){
      setMountedMSG(false);
      referencia.current.scrollTop = referencia.current.scrollHeight;
    };
  }, [ mountedMSG ]);

  const [ todosLosMSGs, settodosLosMSGs ] = useState([]);

  const agregarPrimerMSG = mensajePrimChat => {
    if(mensajePrimChat.mensaje.trim()){
      mensajePrimChat.mensaje = mensajePrimChat.mensaje.trim()
      setMountedMSG(true);
      const primerosMSGsActualizados = [ ...todosLosMSGs, mensajePrimChat];
      settodosLosMSGs(primerosMSGsActualizados);
      seta(1);
    };
  }; 


  const agregarSegundoMSG = mensajeSeguChat => {
    if(mensajeSeguChat.mensaje.trim()){
      mensajeSeguChat.mensaje = mensajeSeguChat.mensaje.trim();
      setMountedMSG(true);
      const segundosMSGsActualizados = [ ...todosLosMSGs, mensajeSeguChat]
      settodosLosMSGs(segundosMSGsActualizados);
      seta(2);
    };
  };


  useEffect(() => {
    if(mountedMSG === true){
      console.log(todosLosMSGs);
      setMountedMSG(false);
    }
  }, [ mountedMSG, todosLosMSGs ]);

  return(
    <div
    className='Chat'>
      <div className="ContenedorChats">
        <FormularioPrimerChat
          onSubmit = {agregarPrimerMSG}/>
        <FormularioSegundoChat
          onSubmit= {agregarSegundoMSG}/>
      </div>
      <div className="RegistroChat">
        <div
          className="contenedorHistorial"
          ref={referencia}>
          {
            todosLosMSGs.map((mensajePrimChat, mensajeSeguChat) => {
              if(mensajePrimChat.tipo === "primero"){
                return(
                  <MensajePrimChat
                  key={mensajePrimChat.id}
                  id = {mensajePrimChat.id}
                  fecha= {mensajePrimChat.fecha}
                  tipo = {mensajePrimChat.tipo}
                  mensaje= {mensajePrimChat.mensaje}
                />
                );
              }else{
                return(
                  <MensajeSeguChat
                    key = {mensajeSeguChat.id}
                    id = {mensajeSeguChat.id}
                    fecha = {mensajeSeguChat.fecha}
                    tipo = {mensajeSeguChat.tipo}
                    mensaje = {mensajeSeguChat.mensaje}
                  />
                );
              }
            })
          }

          {
            /*

              */
          }
        </div>
      </div>
    </div>
  )
};

export default ListaChat;
